import React from "react";
import Card from 'react-bootstrap/Card';
const Cards = ({ pokemon, loading,infoPokemon}) => {
   // console.log(pokemon);
    return (
        <>
        {
            loading ? <h1>Loading...</h1> :
                pokemon.map((item) => {
                    return (
                        <>



                            <Card  className="bg-dark text-white">style={{ width: '18rem' }}  key={item.id} onClick={()=>infoPokemon(item)}>
      <Card.Img  src={item.sprites.front_default} alt="Card image" />
      <Card.ImgOverlay>
      <Card.Body>
        <Card.Title>{item.id}</Card.Title>
        <Card.Text>
        {item.name}
        </Card.Text>

      </Card.Body>
      </Card.ImgOverlay>
    </Card>
                        </>
                    )
                })
        }

        </>
    )
}
export default Cards;
